    var multi_select = form.getFromParent("PA, Neuropathic pain evaluation questionnaire (DN4)").currentRecord.value.value;
    var length = multi_select == undefined ? 0 : multi_select.length;
    var options = [];
    for (var i = 0; i < length; i++) {
      var option = multi_select[i].displayString == undefined ? multi_select[i].name : multi_select[i].displayString;
      options.push(option);
    }
    form.get('PA, DN4 Score').setValue(options.length.toString());
    form.get('PA, Physical and social functioning comment').getValue() || form.get('PA, Physical and social functioning comment').hideAndClear(true);
