    form.get('PA, Mood disorder comment').getValue() || form.get('PA, Mood disorder comment').hideAndClear(true);
    form.get('Nonverbal Assessment').hideAndClear(true);
  } else if (selectedValue === "Algoplus Scale") {
    form.get('Nonverbal Assessment').setHidden(false);
    form.get('Verbal Self Assessment').hideAndClear(true);
  } else {
    form.get('Verbal Self Assessment').hideAndClear(true);
    form.get('PA, Response to medication').hideAndClear(true);
    form.get('PA, Physical and social functioning comment').hideAndClear(true);
    form.get('PA, Sleep disorder comment').hideAndClear(true);
    form.get('PA, Mood disorder comment').hideAndClear(true);
    form.get('Nonverbal Assessment').hideAndClear(true);
  }
