select concept_id into @concept_id from concept_name where name = "BF, Location of Death" and
concept_name_type = "FULLY_SPECIFIED" and locale = "en" and voided = 0;
set @child1_concept_id = 0;
set @child2_concept_id = 0;
set @child3_concept_id = 0;
select concept_id into @child1_concept_id from concept_name where name ="Home" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child2_concept_id from concept_name where name ="Inpatient department" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child3_concept_id from concept_name where name ="Other" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
call add_concept_answer(@concept_id, @child1_concept_id, 1);
call add_concept_answer(@concept_id, @child2_concept_id, 2);
call add_concept_answer(@concept_id, @child3_concept_id, 3);

