select concept_id into @concept_id from concept_name where name = "IC, Previously received oncology treatment" and
concept_name_type = "FULLY_SPECIFIED" and locale = "en" and voided = 0;
set @child1_concept_id = 0;
set @child2_concept_id = 0;
set @child3_concept_id = 0;
set @child4_concept_id = 0;
select concept_id into @child1_concept_id from concept_name where name ="Chemotherapy" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child2_concept_id from concept_name where name ="Radiation therapy" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child3_concept_id from concept_name where name ="Oncosurgery" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child4_concept_id from concept_name where name ="Other" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
call add_concept_answer(@concept_id, @child1_concept_id, 1);
call add_concept_answer(@concept_id, @child2_concept_id, 2);
call add_concept_answer(@concept_id, @child3_concept_id, 3);
call add_concept_answer(@concept_id, @child4_concept_id, 4);

