select concept_id into @concept_id from concept_name where name = "SC, Reason for visit" and
concept_name_type = "FULLY_SPECIFIED" and locale = "en" and voided = 0;
set @child1_concept_id = 0;
set @child2_concept_id = 0;
set @child3_concept_id = 0;
set @child4_concept_id = 0;
set @child5_concept_id = 0;
select concept_id into @child1_concept_id from concept_name where name ="Diagnosis Disclosure" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child2_concept_id from concept_name where name ="Treatment" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child3_concept_id from concept_name where name ="Follow-up care after treatment" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child4_concept_id from concept_name where name ="Signs and symptoms" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
select concept_id into @child5_concept_id from concept_name where name ="Other" and concept_name_type ="FULLY_SPECIFIED" and locale ="en" and voided = 0;
call add_concept_answer(@concept_id, @child1_concept_id, 1);
call add_concept_answer(@concept_id, @child2_concept_id, 2);
